import { useState,useEffect,useRef } from 'react';
import { Button } from 'primereact/button';

const Seat = (props) => {
    const[status, setStatus] = useState(0);
    
    const onClick = () =>{
        console.log(props.labelIndex+' '+status);
        if(status == 0) setStatus(1);
        else if(status == 1) setStatus(0);
    }

    const getColorByStatus = () =>{
        if(status == 0)
            return 'p-button-outlined';
        else if(status == 1)
            return '';
        else if(status == 2)
            return '';
    }

    return (
        <div>
            <Button label={props.labelIndex} className={'p-mr-1 p-button-info'+getColorByStatus()}  onClick={onClick} style={{width:'50px'}} disabled={status == 2 ? true : false}/>
        </div>
    )
}

export default Seat
