                <Button label={data[2].label} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(0)}/>
                <Button label={data[3].label} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(0)}/>
                <Button label={data[4].label} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(0)}/>
                <Button label={data[5].label} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(0)}/>
                <Button label={data[6].label} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(0)}/>
            </div>
            <div className="p-grid">
                <Button />
            </div>
            <Footer/>
        </div>
    )
}

