import { Panel } from 'primereact/panel';
import React, { useState,useEffect } from 'react';
import API from '../utils/request'
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { MultiSelect } from 'primereact/multiselect';
import { Dropdown } from 'primereact/dropdown';

const Category = () => {

    const [data, setData] = useState({});
    const [selectedList, setSelectedList] = useState({});

    useEffect(() => {
        API.get('/api/admin/category', 
        {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
        }})
            .then((res) =>{
                setData(res.data.data);
            })
            .catch((error)=>{
                console.log(error);
            })
    }, [])

    const setSelectedRowCustomize = (e) => {
        setSelectedList(e.value);
    };
    
    return (
        <Panel header="Danh sách thể loại phim">
            <div className="card">
                    <DataTable value={data} paginator rows={10}
                        selection={selectedList} 
                        onSelectionChange={setSelectedRowCustomize} 
                        selectionMode="single" 
                        dataKey="id"
                        stateStorage="local" 
                        stateKey="dt-state-demo-local"
                        scrollable 
                        emptyMessage="Không có thể loại nào">
                        <Column field="name" header="Name" sortable filter filterPlaceholder="Search by name"></Column>
                        <Column field="createdDate" header="Name" sortable filter filterPlaceholder="Search by name"></Column>
                        <Column field="createdBy" header="Name" sortable filter filterPlaceholder="Search by name"></Column>
                        <Column field="updateDate" header="Name" sortable filter filterPlaceholder="Search by name"></Column>
                        <Column field="updateBy" header="Name" sortable filter filterPlaceholder="Search by name"></Column>
                    </DataTable>
            </div>
        </Panel>
    )
}
export default Category;
