import Header from '../../Header/Header';
import Footer from '../../../components/Footer/Footer';
import { useState,useEffect,useRef } from 'react';
import API from '../../utils/request';
import { useParams } from "react-router-dom";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import { toDateTimeString } from "../../utils/date";
import { set } from 'immer/dist/internal';

function UserSessionDetail() {
    const { promiseInProgress } = usePromiseTracker();
    const params = useParams();
    const [data, setData] = useState({});

    useEffect(() => {
        // trackPromise(API.get('/api/public/session/'+params.id))
        // .then((res) =>{
        //     console.log(res.data.data[0]);
        //     setData(res.data.data[0]);    
        // })
        API.get('/api/public/session/'+params.id)
            .then((res) =>{
              setData(res.data.data[0]);
            })
            .catch((error)=>{
                console.log(error);
        });
    }, [])

    return (<>
        <div className="body-container">
            <Header/>   
            {
                promiseInProgress ? <h2>Loading</h2> : 
                (
                    <div className="p-grid">
                        <div className ="p-col-7">
                            <div className="p-grid p-col-12">
                                <div className="p-col-6">
                                    Tên phim: {data.movie.name}
                                </div>
                                <div className="p-col-6">
                                    Ngày giờ: {toDateTimeString(data.showTime)}
                                </div>
                            </div>
                            <div className="p-grid p-col-12">
                                <div className="p-col-6">
                                    Rạp số: {data.cinema.name}
                                </div>
                                <div className="p-col-6">
                                    Loại rạp: {data.cinema.type}
                                </div>
                            </div>
                        </div>
                        <div className="p-col-5" style={{backgroundColor:'white', color:'black'}}>
                            <div className="p-grid" style={{justifyContent:'center'}}><h2>Thông tin hoá đơn</h2></div>
                        </div>
                    </div>
                )

            }       
            <Footer/>
        </div>
    </>
    )
}

export default UserSessionDetail
