import React, { useState,useRef } from 'react';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { useFormik } from 'formik';
import { Toast } from 'primereact/toast';
import API from '../utils/request'
import { useHistory } from "react-router-dom";

function Register() {
    const toast = useRef(null);
    const [data, setData] = useState({});
    const [selectedList, setSelectedList] = useState({});
    const [isInsert, setIsInsert] = useState(true);
    const [showDetail, setShowDetail] = useState([]);
    const [sex, setSex] = useState(true);
    const history = useHistory();

    const formik = useFormik({
        initialValues: {
            id: '',
            username : '',
            password : '',
            fullName : '',
            name : '',
            sex : '',
            phone : '',
            address : ''
        },
        validate: (data) => {
            const errors = {};
            if(!data.username.trim()){
                errors.username = 'Username không được để trống';
            }
            if(isInsert){
                if(!data.password.trim()){
                    errors.password = 'Password không được để trống';
                }
            }
            if(!data.fullName.trim()){
                errors.fullName = 'Họ tên không được để trống';
            }
            if(!data.phone.trim()){
                errors.phone = 'Số điện thoại không được để trống';
            }
            return errors;
        },
        onSubmit: (data) => {
            console.log(data);
            let API_URL = (isInsert ? '/api/auth/signup/staff' : '/api/admin/staff');
            data.sex = sex;
            API.post(API_URL, data, 
            {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
            }})
                .then((res) =>{
                    if(res.data.ok){
                        toast.current.show({severity:'success', summary: 'Thông báo', detail: res.data.message, life: 3000});
                        
                    }else{
                        toast.current.show({severity:'warn', summary: 'Thông báo', detail: res.data.message, life: 3000});
                    }
                    
                })
                .catch((error)=>{
                    console.log(error);
                    toast.current.show({severity:'error', summary: 'Thông báo', detail:'Lỗi hệ thống', life: 3000});
            });
        }
    });

    return (
        <div>
        <Toast ref={toast} />
        <div className="form">
            <div className="p-d-flex p-jc-center">
                <div className="card">
                    <h2 className="p-text-center">Đăng nhập</h2>
                    <form onSubmit={formik.handleSubmit} className="p-fluid">
                        <div className="p-field">
                            <span className="p-float-label">
                                <InputText id="username" name="username" value={formik.values.username} onChange={formik.handleChange} autoFocus />
                                <label htmlFor="username" className={classNames({ 'p-error': isFormFieldValid('username') })}>Username</label>
                            </span>
                            {getFormErrorMessage('username')}
                        </div>
                        <div className="p-field">
                            <span className="p-float-label">
                                <InputText type="password" id="password" name="password" value={formik.values.password} onChange={formik.handleChange} autoFocus />
                                <label htmlFor="password" className={classNames({ 'p-error': isFormFieldValid('password') })}>Password</label>
                            </span>
                            {getFormErrorMessage('password')}
                        </div>
                        <Button type="submit" label="Đăng nhập" className="p-mt-2" />
                        <div className="p-grid p-mt-3" style={{justifyContent:'center'}}>
                            <a href="/register" >Chưa có tài khoản, Đăng ký</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
        </div>
    )
}

export default Register
