import { Card } from 'primereact/card';
import { Panel } from 'primereact/panel';
import { Chart } from 'primereact/chart';
import React, { useState,useEffect } from 'react';
import { numberFormat } from "../utils/number";
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import API from '../utils/request';
function Dashboard() {
    const [data, setData] = useState({});
    const { promiseInProgress } = usePromiseTracker();

    useEffect(() => {
        trackPromise(API.get('/api/public/session/'+params.id))
        .then((res) =>{
            if(res.data.ok){
                setData(res.data.data[0]);    
            }
            
        })
    }, [])

    const multiAxisData = {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [{
            label: 'Dataset 1',
            fill: false,
            borderColor: '#42A5F5',
            yAxisID: 'y',
            tension: .4,
            data: [650000, 59, 80, 81, 56, 55, 10000]
        }, {
            label: 'Dataset 2',
            fill: false,
            borderColor: '#00bb7e',
            yAxisID: 'y1',
            tension: .4,
            data: [28, 48, 40, 19, 86, 27, 90]
        }]
    };

    const getLightTheme = () => {
        let multiAxisOptions = {
            stacked: false,
            maintainAspectRatio: false,
            aspectRatio: .6,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y: {
                    type: 'linear',
                    display: true,
                    position: 'left',
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y1: {
                    type: 'linear',
                    display: true,
                    position: 'right',
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        drawOnChartArea: false,
                        color: '#ebedef'
                    }
                }
            }
        };

        return {
            multiAxisOptions
        }
    }

    const { multiAxisOptions} = getLightTheme();

    return (
        <Panel header="Thống kê" style={{width:'100%', justifyContent:'center'}}>
            <div className="p-grid" style={{justifyContent:'center'}} >
                <div className="p-col">
                    <Card title="Doanh thu" style={{ width: '30rem', marginBottom: '2em' }}>
                        1,000,000,000VND
                    </Card>
                </div>
                <div className="p-col">
                    <Card title="Số vé bán" style={{ width: '30rem', marginBottom: '2em' }}>
                        1,000,000
                    </Card>
                </div>
                <div className="p-col">
                    <Card title="Người dùng" style={{ width: '30rem', marginBottom: '2em' }}>
                        10
                    </Card>
                </div>
            </div>    
            <div className="p-grid p-col-12" style={{width:'100%'}}>
                <div className="card" style={{width:'100%'}}>
                    <h5>Doanh thu</h5>
                    <Chart type="line" data={multiAxisData}  options={multiAxisOptions} />
                </div>
            </div>
        </Panel>
    )
}

export default Dashboard
