import Header from '../../Header/Header';
import Footer from '../../../components/Footer/Footer';
import { Button } from 'primereact/button';
import { useState,useEffect,useRef } from 'react';
import API from '../../utils/request';

function UserSession() {
    const [index, setIndex] = useState(0);
    const [data, setData] = useState([]);

    useEffect(() => {
        let lstDate = [];
        for (let i = 0; i < 7; i++) {
            let date = new Date();
            date.setDate(date.getDate() + i);
            let d = {
                id : i,
                value: date.getFullYear()+'-'+(date.getMonth()+1)+'-'+date.getDate(),
                label: getDayOfWeek(date) +' '+date.getDate()+'/'+date.getMonth()
            };
            lstDate.push(d);
        }
        setData(lstDate);    
    }, []);

    useEffect(() => {
        let date = new Date();
        let body = {
            date : (data.length == 0 ? '' : data[index].value),
            time : ''
        }

        API.post('/api/public/session/inday', body)
            .then((res) =>{
              if(res.data.data == null){
                  setData({});
              }else{
                  if(res.data.ok){
                      setData(res.data.data[0]);
                  }else setData({});
              }
            })
            .catch((error)=>{
                console.log(error);
        });
    }, [index])

    const getDayOfWeek = (day) =>{
        return (day.getDay() === 0 ? 'Chủ nhật' : 'Thứ '+(day.getDay()+1));
    }

    const showLstButton = () => {
        return (
            <div>
                {data.map((ele, index) => {
                    return (<Button label={ele.label} key={index} className="p-button-rounded p-button-outlined p-button-success p-mr-5 p-mb-2"  onClick={() => setIndex(ele.id)}/>);
                })}
            </div>
        );
    }



    return (
        <div className="body-container">
            <Header/>          
            <div className="p-grid" style={{justifyContent: 'center'}}>
                {showLstButton()}
            </div>
            <div className="p-grid">
                
                    <Button/>
                <br style={{color: 'white'}}/>
            </div>
            <Footer/>
        </div>
    )
}

export default UserSession
