import { Panel } from 'primereact/panel';
import React, { useState,useEffect,useRef } from 'react';
import QrReader from 'react-qr-reader'

export default function Receipt() {
    const [result, setResult] = useSate('Not Found');
     
    const handleScan = data => {
        if (data) {
          setResult(data);
        }
      }
    const handleError = err => {
        console.error(err)
    }

    return (
        <Panel header="Danh sách hoá đơn">
            <QrReader
                delay={300}
                onError={handleError}
                onScan={handleScan}
                style={{ width: '100%' }}
            />
            <p>{result}</p>
        </Panel>
    )
}
