import React from "react";
import Sidebar from "../../Sidebar/Sidebar";
import "./Layout.css";
import logo from '../../../assets/icon-web.jpg';
import fakeAvatar from '../../../assets/fake-avatar.jpg';
import { Avatar } from 'primereact/avatar';
import { Button } from 'primereact/button';
function Layout(props) {
    const leftChildren = [];
    const rightChildren = [];

    React.Children.forEach(props.children, (child) => {
        if (child?.type === Sidebar) {
            leftChildren.push(child);
        } else {
            rightChildren.push(child);
        }
    });

    return (
        <div className="layout">
            <div className="layout-sidebar" style={{backgroundColor:'MenuText', color: 'white'}}>
                <div className="flex-top">
                    <div className="sidebar-head">
                        <img src={logo} style={{ width: 200, height: 50 }} />
                    </div>
                    <div className="sidebar-list"> {leftChildren}</div>
                </div>
                <div className="sidebar-bottom">
                    <div className="p-gird">
                        <Avatar label="P" shape="circle"/>
                        <span className="sidebar-name">{localStorage.getItem('username')}</span>
                    </div>
                    <div className="p-gird">
                        <Button label="Đăng xuất" icon="pi pi-book" className="p-button-dangger"/>
                    </div>
                    
                </div>
            </div>
            <div className="layout-content">{rightChildren}</div>
        </div>
    )
}

Layout.Sidebar = Sidebar;

export default Layout;
