import { Card } from 'primereact/card';
import { Panel } from 'primereact/panel';
import { Chart } from 'primereact/chart';
function Dashboard() {
    const basicData = {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [
            {
                label: 'First Dataset',
                data: [65, 59, 80, 81, 56, 55, 40],
                fill: false,
                borderColor: '#42A5F5',
                tension: .4
            },
            {
                label: 'Second Dataset',
                data: [28, 48, 40, 19, 86, 27, 90],
                fill: false,
                borderColor: '#FFA726',
                tension: .4
            }
        ]
    };

    const getLightTheme = () => {
        let basicOptions = {
            maintainAspectRatio: false,
            aspectRatio: .6,
            plugins: {
                legend: {
                    labels: {
                        color: '#495057'
                    }
                }
            },
            scales: {
                x: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                },
                y: {
                    ticks: {
                        color: '#495057'
                    },
                    grid: {
                        color: '#ebedef'
                    }
                }
            }
        };
        return {
            basicOptions
        }
    }

    const { basicOptions } = getLightTheme();

    return (
        <>
            <div className="p-grid p-col-12" style={{justifyContent:'space-between'}} >
                <div className="p-col">
                    <Card title="Doanh thu" style={{ width: '30rem', marginBottom: '2em' }}>
                        1,000,000,000VND
                    </Card>
                </div>
                <div className="p-col">
                    <Card title="Số vé bán" style={{ width: '30rem', marginBottom: '2em' }}>
                        1,000,000
                    </Card>
                </div>
                <div className="p-col">
                    <Card title="Người dùng" style={{ width: '30rem', marginBottom: '2em' }}>
                        10
                    </Card>
                </div>
            </div>    
            <div className="p-grid p-col-12" style={{width:'100%'}}>
                <div className="card" style={{width:'100%'}}>
                    <h5>Doanh thu</h5>
                    <Chart type="line" data={basicData}  options={basicOptions} />
                </div>
            </div>
        </>
    )
}

export default Dashboard
