import Header from '../../Header/Header';
import Slideshow from '../../Slideshow/Slideshow';
import React, { useState,useEffect } from 'react';
import { DataView, DataViewLayoutOptions } from 'primereact/dataview';
import API from '../../utils/request';
import './UserHome.css'
import { APPCONFIG } from '../../utils/constants';
import { Button } from 'primereact/button';
function UserHome() {
    const [movies, setMovies] = useState(null);

    useEffect(() => {
        API.get('/api/public/movie/is-showing')
            .then((res) =>{
                setMovies(res.data.data);
                console.log(res.data.data);
            })
            .catch((error)=>{
                console.log(error);
        });
    }, [])

    const renderGridItem = (data) => {
        return (
            <div className="p-col-12 p-md-4">
                <div className="product-grid-item card">
                    <div className="product-grid-item-top">
                        <div>
                            <i className="pi pi-tag product-category-icon"></i>
                            <span className="product-category">{data.category}</span>
                        </div>
                        <span className={`product-badge status-${data.inventoryStatus}`}>{data.inventoryStatus}</span>
                    </div>
                    <div className="product-grid-item-content">
                    <img src={`${APPCONFIG.BASE_URL_IMAGE}${data.pathThumbnail}`} 
                        onError={(e) => e.target.src='https://www.primefaces.org/wp-content/uploads/2020/05/placeholder.png'} alt={data.name} />
                        <div className="product-name">{data.name}</div>                        
                    </div>
                    <div className="product-grid-item-bottom">
                        <Button icon="pi pi-shopping-cart" label="Xem chi tiết" disabled={data.inventoryStatus === 'OUTOFSTOCK'}></Button>
                        <Button icon="pi pi-shopping-cart" label="Mua vé" disabled={data.inventoryStatus === 'OUTOFSTOCK'}></Button>
                    </div>
                </div>
            </div>
        );
    }

    const itemTemplate = (product, layout) => {
        return renderGridItem(product);
    }

    return (
        <div>
            <Header/>
            <Slideshow/>
            <div className='p-grid'>
                <div className='p-col-12' style={{marginTop: '200px'}}>
                    
                    
                        <div className="dataview-demo">
                            <div className="card">
                                <DataView value={movies}
                                    itemTemplate={itemTemplate} paginator rows={9}/>
                            </div>
                        </div>
                    
                    
                </div>
            </div>
            
        </div>
    )
}

export default UserHome
